securityRules.rules:
rules_version = '2';
service cloud.firestore {
  match /databases/{database}/documents {
    // Helper functions
    function isAuthenticated() {
      return request.auth != null;
    }

    function hasRole(role) {
      return isAuthenticated() && 
        get(/databases/$(database)/documents/users/$(request.auth.uid)/roles/$(role)).data != null;
    }

    function isMasterAdmin() {
      return hasRole('MasterAdmin');
    }

    function isStaff() {
      return hasRole('MasterAdmin') || hasRole('Director') || hasRole('Account');
    }

    // Users collection
    match /users/{userId} {
      allow read: if request.auth.uid == userId || isMasterAdmin();
      allow update: if request.auth.uid == userId;
      allow create: if isMasterAdmin();
      allow delete: if false;

      match /roles/{roleId} {
        allow read: if request.auth.uid == userId || isMasterAdmin();
        allow write: if isMasterAdmin();
      }
    }

    // Brands collection
    match /brands/{brandId} {
      allow read: if isAuthenticated();
      allow write: if isStaff();

      match /campaigns/{campaignId} {
        allow read: if isAuthenticated();
        allow create, update: if isStaff();
        allow delete: if hasRole('MasterAdmin') || hasRole('Director');
      }
    }

    // Channels collection
    match /channels/{channelId} {
      allow read: if isAuthenticated();
      allow write: if isMasterAdmin();
    }

    // Reference data (read-only for authenticated users)
    match /{collection}/{docId} {
      allow read: if collection in ['departments', 'jobTitles', 'features', 
        'campaignTypes', 'serviceTypes', 'metricDefinitions', 'channelCategories'];
      allow write: if isMasterAdmin();
    }
  }
}
